mapper.select:
select

from tb_enterprise
where product_id = #{productId,jdbcType=INTEGER}
and enterprise_name = #{enterpriseName,jdbcType=VARCHAR}
and approval_announcement_no_enterprise = #{approvalAnnouncementNoEnterprise,jdbcType=VARCHAR}

mapper.delete:
delete from tb_enterprise
where product_id = #{productId,jdbcType=INTEGER}
  and enterprise_name = #{enterpriseName,jdbcType=VARCHAR}
  and approval_announcement_no_enterprise = #{approvalAnnouncementNoEnterprise,jdbcType=VARCHAR}

mapper.select:
select id,enterprise_name from tb_enterprise where is_first_submission = '未变更' and
id in

    #{item}

select substr(classificationId, 1, 2) AS pieArea, count(1) AS countNum from tb_enterprise en left join tb_product pro on en.product_id = pro.id where is_first_submission = '未变更'
AND en.approval_year = #{year}
AND administrative_id IN (SELECT city_id FROM tb_administrative_area WHERE parent_id = #{parentId} )
AND administrative_id = #{parentId}
AND substr(classificationId, 1, 2) = #{classification}
GROUP BY substr(classificationId, 1, 2) ORDER BY count(1) DESC
